import React, { useContext, useState } from "react";
import CurrencyInput from "react-currency-input-field";
import { AppContext } from "../context/AppContext";
import { FaSave } from "react-icons/fa";
import Swal from "sweetalert2";
import "../App.css";

const AllocationForm = (props) => {
  // useContext returns the context value for the context you passed
  // and store the values to expenses locally
  const { currency, dispatch, remaining } = useContext(AppContext);

  const [name, setName] = useState("");
  const [cost, setCost] = useState(0);
  const [action, setAction] = useState("");

  const submitEvent = (event) => {
    // validate if allocation cost is for than remaining; if so set clear cost value
    if (cost < 1 || !name.trim()) {
      Swal.fire({
        position: "top",
        title: `Allocation or Department`,
        text: `Can not be $0 or empty`,
        icon: "error",
      });

      setCost(0);
      return;
    } else if (cost > remaining && !name.trim()) {
      Swal.fire({
        position: "top",
        title: `Allocation ${currency}` + cost,
        text:
          `The value cannot exceed remaining budget ${currency}` + remaining,
        icon: "error",
      });

      setCost(0);
      return;
    }

    const expense = {
      name: name,
      cost: parseInt(cost),
    };

    if (action === "Reduce") {
      dispatch({
        type: "RED_EXPENSE",
        payload: expense,
      });

      Swal.fire({
        position: "top",
        title: `Allocation ${currency}` + cost,
        text: "decrease successfully",
        icon: "success",
        showConfirmButton: false,
        timer: 1500,
      });

      setName("");
      setCost(0);
    } else {
      dispatch({
        type: "ADD_EXPENSE",
        payload: expense,
      });

      Swal.fire({
        position: "top",
        title: `Allocation ${currency}` + cost,
        text: "Increase successfully",
        icon: "success",
        showConfirmButton: false,
        timer: 1500,
      });

      setName("");
      setCost(0);
    }

  };

  return (
    <div className="budge-panel">
      <div className="row">
        <div className="input-group mb-3" style={{ marginLeft: "2rem" }}>
          <div className="input-group-prepend">
            <label className="input-group-text" htmlFor="inputGroupSelect01">
              Department
            </label>
          </div>
          <select value={name} onChange={(e) => setName(e.target.value)}>
          <option>Select</option>
          {data &&
            data.map((d, index) => {
              return <option key={index}>{d}</option>;
            })}
        </select>

          <div className="input-group-prepend" style={{ marginLeft: "2rem" }}>
            <label className="input-group-text" htmlFor="inputGroupSelect02">
              Allocation
            </label>
          </div>
          <select
            className="custom-select"
            id="inputGroupSelect02"
            onChange={(event) => setAction(event.target.value)}
          >
            <option defaultValue value="Add" name="Add">
              Add
            </option>
            <option value="Reduce" name="Reduce">
              Reduce
            </option>
          </select>
          <div className="allocate-input">
            <span style={{ marginLeft: "2rem" }}>{currency}</span>
            <CurrencyInput
              label="Amount"
              className="number"
              id="cost"
              name="cost"
              step={1000}
              defaultValue={0}
              value={cost}
              allowDecimals={true}
              decimalsLimit={2}
              onValueChange={(value) => setCost(value)}
            />
          </div>
          <button
            className="btn btn-primary"
            onClick={submitEvent}
            style={{ marginLeft: "2rem" }}
          >
            Save <FaSave />
          </button>
          <button onClick={() => setName("")}>Clear</button>
        </div>
      </div>
    </div>
  );
};

export default AllocationForm;
