import React, { useContext } from "react";
import { AppContext } from "../context/AppContext";
import Swal from "sweetalert2";
import "../App.css";

const Currency = () => {
  const { currency, dispatch } = useContext(AppContext);

  const changeCurrency = (val) => {
    dispatch({
      type: "CHG_CURRENCY",
      payload: val,
    });

    Swal.fire({
      position: "top",
      icon: "success",
      title: `${currency} Replaced`,
      text: "Currency change successfully",
      showConfirmButton: false,
      timer: 1500
    });
  };

  return (
    <div className="alert alert-info">
      <label htmlFor="currency">Currency </label>
      <select
        id="dropdown"
        name="currency"
        value={currency}
        onChange={(event) => changeCurrency(event.target.value)}
      >
        <option className="money" value="$">$ Dollar</option>
        <option className="money" value="£">£ Pound</option>
        <option className="money" value="€">€ Euro</option>
        <option className="money"  value="₹">₹ Rupee</option>
      </select>


      <div class="select">
<select name="you_are" id="dropdown" class="selection">
<option value="0" disabled selected>Select</option>
<option value="1">Student</option>
<option value="2">Full-time Job</option>
<option value="2">Part-time Job</option>
<option value="3">Job-Seeker</option>
<option value="4">Nothing Yet</option>
</select>
</div>

    </div>
  );
};


export default Currency;
