import React, { useContext, useState } from "react";
import CurrencyInput from "react-currency-input-field";
import Select from "react-select";
import { useForm, Controller } from "react-hook-form";
import { AppContext } from "../context/AppContext";
import { FaSave } from "react-icons/fa";
import Swal from "sweetalert2";
import "../App.css";

const AllocationForm = (props) => {
  // useContext returns the context value for the context you passed
  // and store the values to expenses locally
  const { currency, dispatch, options, remaining } = useContext(AppContext);

  const { control, setValue } = useForm();

  const [name, setName] = useState("");
  const [cost, setCost] = useState(0);
  const [action, setAction] = useState("");

  const handleDepartment = (event) => {
    event.preventDefault();
    setName(event.target.value);
  };

  const handleReset = () => {
    setValue("departmentSelect", { value: "", label: "Select..." });
    setCost(0);
  };

  const submitEvent = () => {
    // validate if allocation cost is for than remaining; if so set clear cost value
    if (cost < 1 || !name) {
      Swal.fire({
        position: "top",
        title: `Allocation or Department`,
        text: `Can not be $0 or empty`,
        icon: "error",
      });

      return;
    } else if (cost > remaining && !name) {
      Swal.fire({
        position: "top",
        title: `Allocation ${currency}` + cost,
        text:
          `The value cannot exceed remaining budget ${currency}` + remaining,
        icon: "error",
      });

      return;
    }

    const expense = {
      name: name,
      cost: parseInt(cost),
    };

    if (action === "Reduce") {
      dispatch({
        type: "RED_EXPENSE",
        payload: expense,
      });

      Swal.fire({
        position: "top",
        title: `Allocation ${currency}` + cost,
        text: "decrease successfully",
        icon: "success",
        showConfirmButton: false,
        timer: 1500,
      });

    } else {
      dispatch({
        type: "ADD_EXPENSE",
        payload: expense,
      });

      Swal.fire({
        position: "top",
        title: `Allocation ${currency}` + cost,
        text: "Increase successfully",
        icon: "success",
        showConfirmButton: false,
        timer: 1500,
      });

      // setName("");
      // setCost(0);
    }
  };

  return (
    <div className="budge-panel">
      <div className="row">
        <div className="input-group mb-3" style={{ marginLeft: "2rem" }}>
          <div className="input-group-prepend">
            <label className="input-group-text" htmlFor="departmentSelect">
              Department
            </label>
          </div>{" "}

              <Select
              className="custom-select"
            id="departmentSelect"
            name="departmentSelect"
            styles={{ width: "20px" }}
                value={name}
                options={options}
                onChange={handleDepartment}
              />

          <div className="input-group-prepend" style={{ marginLeft: "2rem" }}>
            <label className="input-group-text" htmlFor="inputGroupSelect02">
              Allocation
            </label>
          </div>
          <select
            className="custom-select"
            id="inputGroupSelect02"
            onChange={(event) => setAction(event.target.value)}
          >
            <option defaultValue value="Add" name="Add">
              Add
            </option>
            <option value="Reduce" name="Reduce">
              Reduce
            </option>
          </select>
          <div className="allocate-input">
            <span style={{ marginLeft: "2rem" }}>{currency}</span>
            <CurrencyInput
              className="number"
              id="cost"
              name="cost"
              step={1000}
              defaultValue={0}
              value={cost}
              allowDecimals={true}
              decimalsLimit={2}
              onValueChange={(newCost) => setCost(newCost)}
            />
          </div>
          <button
            className="btn btn-primary"
            onClick={submitEvent}
            style={{ marginLeft: "2rem" }}
          >
            Save <FaSave />
          </button>
          <button type="button" onClick={handleReset}>
            Reset
          </button>
        </div>
      </div>
    </div>
  );
};

export default AllocationForm;
