import React, { useContext } from "react";
import Dropdown from "react-bootstrap/Dropdown";
import { AppContext } from "../context/AppContext";
import "../App.css";

const Currency = () => {
  const { currency, dispatch } = useContext(AppContext);

  const changeCurrency = (val) => {
    dispatch({
      type: "CHG_CURRENCY",
      payload: val,
    });
  };

  return (
    <Dropdown>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        Dropdown Button
      </Dropdown.Toggle>
      <Dropdown.Menu id="currency"
        name="currency"
        value={currency}
        onChange={(event) => changeCurrency(event.target.value)}
      >
        <Dropdown.Item href="#/action-1" value="$">$ Dollar</Dropdown.Item>
        <Dropdown.Item href="#/action-2" value="£">£ Pound</Dropdown.Item>
        <Dropdown.Item href="#/action-3" value="€">€ Euro</Dropdown.Item>
        <Dropdown.Item href="#/action-3" value="₹">₹ Rupee</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default Currency;
