import React, { useContext, useState } from "react";
import CurrencyInput from "react-currency-input-field";
import Select from "react-select";
import { AppContext } from "../context/AppContext";
import { FaSave } from "react-icons/fa";
import Swal from "sweetalert2";
import "../App.css";

const AllocationForm = (props) => {
  // useContext returns the context value for the context you passed
  // and store the values to expenses locally
  const { currency, dispatch, options, remaining } = useContext(AppContext);

  const [name, setName] = useState("");
  const [cost, setCost] = useState(0);
  const [action, setAction] = useState(null);

  const actions = [
    { value: "Add", label: "Add" },
    { value: "Reduce", label: "Reduce" },
  ];

  const handleReset = () => {
    setName("departmentSelect", { value: "", label: "" });
    setAction("actionSelect", { value: "", label: "" });
    setCost(0);
  };

  const submitEvent = () => {
    // validate if allocation cost is for than remaining; if so set clear cost value
    if (cost < 1 || !name || !action) {
      Swal.fire({
        position: "top",
        title: `Department, Allocation or Cost`,
        text: `Can not be empty or $0`,
        icon: "error",
      });

      return;
    } else if (cost > remaining && !name) {
      Swal.fire({
        position: "top",
        title: `Allocation ${currency}` + cost,
        text:
          `The value cannot exceed remaining budget ${currency}` + remaining,
        icon: "error",
      });

      return;
    }

    const expense = {
      name: name.value,
      cost: parseInt(cost),
    };

    if (action.value === "Reduce" && name) {
      dispatch({
        type: "RED_EXPENSE",
        payload: expense,
      });

      Swal.fire({
        position: "top",
        title: `Allocation ${currency}` + cost,
        text: "decrease successfully",
        icon: "success",
        showConfirmButton: false,
        timer: 1500,
      });
    } else if (action.value === "Add" && name)  {
      dispatch({
        type: "ADD_EXPENSE",
        payload: expense,
      });

      Swal.fire({
        position: "top",
        title: `Allocation ${currency}` + cost,
        text: "Increase successfully",
        icon: "success",
        showConfirmButton: false,
        timer: 1500,
      });
    }

  };

  return (
    <div className="budge-panel">
      <div className="row">
        <div className="input-group mb-3" style={{ marginLeft: "2rem" }}>
          <div className="input-group-prepend">
            <label className="input-group-text" htmlFor="departmentSelect">
              Department
            </label>
          </div>
          {" "}
          <Select
            isClearable
            className="custom-select"
            id="departmentSelect"
            name="departmentSelect"
            styles={{ width: "2rem" }}
            value={name}
            options={options}
            onChange={(department) => setName(department)}
          />
          <div className="input-group-prepend" style={{ marginLeft: "2rem" }}>
            <label className="input-group-text" htmlFor="actionSelect">
              Allocation-Type
            </label>
          </div>
          <Select
            className="custom-select"
            id="actionSelect"
            name="actionSelect"
            styles={{ width: "2rem" }}
            value={action}
            options={actions}
            onChange={(newAction) => setAction(newAction)}
          />
          <div className="allocate-input">
            <span style={{ marginLeft: "2rem" }}>{currency}</span>
            <CurrencyInput
              className="number"
              id="cost"
              name="cost"
              step={1000}
              defaultValue={0}
              value={cost}
              allowDecimals={true}
              decimalsLimit={2}
              onValueChange={(newCost) => setCost(newCost)}
            />
          </div>
          <button
            className="btn btn-primary"
            onClick={submitEvent}
            style={{ marginLeft: "2rem" }}
          >
            Save <FaSave />
          </button>
          <button type="button" className="btn btn-warning" onClick={handleReset}>
            Reset
          </button>
        </div>
      </div>
    </div>
  );
};

export default AllocationForm;
