import React, { useContext } from "react";
import { AppContext } from "../context/AppContext";
import "../App.css";

const Currency = () => {
  const { currency, dispatch } = useContext(AppContext);

  const changeCurrency = (val) => {
    dispatch({
      type: "CHG_CURRENCY",
      payload: val,
    });
  };

  return (
    <div className="alert alert-info">



  <button type="button" className="btn btn-danger dropdown-toggle" data-bs-toggle="dropdown" aria-expanded="false" value={currency}
        onChange={(event) => changeCurrency(event.target.value)}>
    Action
  </button>
  <ul class="dropdown-menu">
    <li><a className="dropdown-item" href="#">$ Dollar</a></li>
    <li><a className="dropdown-item" href="#">£ Pound</a></li>
    <li><a className="dropdown-item" href="#">€ Euro</a></li>
    <li><a className="dropdown-item" href="#">₹ Rupee</a></li>
  </ul>





      <label htmlFor="currency">Currency </label>
      <select
        id="currency"
        name="currency"
        value={currency}
        onChange={(event) => changeCurrency(event.target.value)}
      >
        <option value="$">$ Dollar</option>
        <option value="£">£ Pound</option>
        <option value="€">€ Euro</option>
        <option value="₹">₹ Rupee</option>
      </select>
    </div>
  );
};

export default Currency;
