import React, { useContext } from "react";
import { AppContext } from "../context/AppContext";
import Swal from "sweetalert2";
import "../App.css";

const Currency = () => {
  const { currency, dispatch } = useContext(AppContext);

  const changeCurrency = (val) => {
    dispatch({
      type: "CHG_CURRENCY",
      payload: val,
    });

    Swal.fire({
      position: "top",
      icon: "success",
      title: currency,
      text: "Currency change successfully",
      showConfirmButton: false,
      timer: 1500
    });
  };

  return (
    <div style={"background-color:grey; height:400px; width: 400px;"}>
   <div class="styled-select">
      <select id="campaignListId" name="campaignId" onmousedown="if(this.options.length>5){this.size=5;}" onchange='this.size=0;' onblur="this.size=0;">
          <option value="1">First</option>
          <option value="2">Second</option>
          <option value="3">Third</option>
          <option value="4">Fourth</option>
          <option value="5">Fifth</option>
          <option value="6">Sixth</option>
          <option value="7">Seventh</option>
      </select>
  </div>
</div>
  );
};


export default Currency;
