import React, { useContext, useState } from "react";
import { AppContext } from "../context/AppContext";
import { FaSave } from "react-icons/fa";
import Swal from "sweetalert2";
import "../App.css";

const AllocationForm = (props) => {
  // useContext returns the context value for the context you passed
  // and store the values to expenses locally
  const { currency, dispatch, remaining } = useContext(AppContext);

  const [name, setName] = useState("");
  const [cost, setCost] = useState("");
  const [action, setAction] = useState("");

  const submitEvent = (event) => {
    // validate if cost is for than remaining; if so set clear cost value
    if (cost > remaining) {
      Swal.fire({
        position: "top",
        title:`Allocation ${currency}` + cost,
        text: (`The value cannot exceed remaining funds ${currency}` + remaining),
        icon: "error"
      });

      //alert(`The value cannot exceed remaining funds ${currency}` + remaining);
      setCost("");
      return;
    }
    else if (Number.isNaN(event.target.value)) {
      Swal.fire({
        position: "top",
        title: "Number values only",
        text: "Your input: " + event.target.value,
        icon: "error"
      });
      setCost("");
      return;
    }

    const expense = {
      name: name,
      cost: parseInt(cost),
    };

    if (action === "Reduce") {
      dispatch({
        type: "RED_EXPENSE",
        payload: expense,
      });

    } else {
      dispatch({
        type: "ADD_EXPENSE",
        payload: expense,
      });
    }

    setCost("");
  };

  return (
    <div className="budge-panel">
      <div className="row">
        <div className="input-group mb-3" style={{ marginLeft: "2rem" }}>
          <div className="input-group-prepend">
            <label className="input-group-text" htmlFor="inputGroupSelect01">
              Department
            </label>
          </div>
          <select
            className="custom-select"
            id="inputGroupSelect01"
            onChange={(event) => setName(event.target.value)}
          >
            <option defaultValue>Choose...</option>
            <option value="Marketing" name="marketing">
              {" "}
              Marketing
            </option>
            <option value="Sales" name="sales">
              Sales
            </option>
            <option value="Finance" name="finance">
              Finance
            </option>
            <option value="HR" name="hr">
              HR
            </option>
            <option value="IT" name="it">
              IT
            </option>
            <option value="Admin" name="admin">
              Admin
            </option>
          </select>


          <select
            className="custom-select"
            id="inputGroupSelect02"
            onChange={(event) => setAction(event.target.value)}
          >
            <option defaultValue value="Add" name="Add">
              Add
            </option>
            <option value="Reduce" name="Reduce">
              Reduce
            </option>
          </select>
          <div className="allocate-input">
            <span style={{ marginLeft: "2rem" }}>{currency}</span>
            <input
              className="number"
              required="required"
              type="number"
              id="cost"
              placeholder="Make allocation..."
              value={cost}

              onChange={(event) => setCost(event.target.value)}
            ></input>
          </div>
          <button
            className="btn btn-primary"
            onClick={submitEvent}
            style={{ marginLeft: "2rem" }}
          >
            Save <FaSave />
          </button>
        </div>
      </div>
    </div>
  );
};

export default AllocationForm;
